sysauto_script.script:
// Get today's time and date
	var rightNow = new GlideDateTime();
	rightNow.getLocalTime();
	var nowPlusOneDay = GlideDateTime();
	nowPlusOneDay.addDaysLocalTime(1);

	gs.info("rightNow: " + rightNow + ", nowPlusOneDay: " + nowPlusOneDay);

	// Query the database for NeedIt Task records with Due date field values older
	// than the current time.  Only return NeedIt Task records that do not have
	// a State field value of Closed Complete.
	var overdueNITasks = new GlideRecord('x_58872_needit_needit_task');
	overdueNITasks.addQuery('due_date', '>=', rightNow);
	overdueNITasks.addQuery('due_date', '<=', nowPlusOneDay);
	overdueNITasks.addQuery('state', '!=', 3);

	overdueNITasks.query();
	//gs.info("overdueNITasks.query();");	
	// Write a log message for each overdue NeedIt Task Record
	while(overdueNITasks.next()){
		gs.info("NeedIt Tasks Due Soon = " + overdueNITasks.number + " (" + overdueNITasks.due_date + ")");
		gs.eventQueue('x_58872_needit.dueSoonNeedItTask',overdueNITasks,overdueNITasks.number,gs.getUserName());
		//gs.info("in der Schleife");	
	}
	//gs.info("ganz am Ende");
